import React,{useState,useEffect} from 'react';
import './App.css';

import Carousel from 'react-bootstrap/Carousel';


function App() {
  const [data,setData]=useState([]);

  const getData=()=>{
    fetch('https://us-central1-resolute-ascent-429512-h0.cloudfunctions.net/get_movies'
    ,{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
    )
      .then(function(response){
        console.log(response)
        return response.json();
      })
      .then(function(myJson) {
        console.log(myJson);
        setData(myJson)
      });
  }
  useEffect(()=>{
    getData()
  },[]);

  // Slider/carousel settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Show 3 cards at a time
    slidesToScroll: 1, // Scroll 1 card at a time
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };
  return (
    <div className="App">
      <h1>Favorite Movies</h1>
      <Carousel>
        {data &&
          data.length > 0 &&
          data.map((item, index) => (
            <Carousel.Item key={index}>
              <img className="d-block w-100" src={item.image_url} alt={item.title} />
              <Carousel.Caption>
                <h3 className="movie-title">Movie name: {item.title}</h3>
                <p className="movie-details">Genre: {item.genre}</p>
                <p className="movie-details">Rating: {item.rating}</p>
                <p className="movie-details">Release year: {item.release_year}</p>
                <p className="movie-details">Director: {item.director}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
      </Carousel>
    </div>
  );
}

export default App;
