import React,{useState,useEffect} from 'react';
import './App.css';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


function App() {
  const [data,setData]=useState([]);

  const getData=()=>{
    fetch('https://us-central1-resolute-ascent-429512-h0.cloudfunctions.net/get_movies'
    ,{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
    )
      .then(function(response){
        console.log(response)
        return response.json();
      })
      .then(function(myJson) {
        console.log(myJson);
        setData(myJson)
      });
  }
  useEffect(()=>{
    getData()
  },[]);

  // Slider/carousel settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Show 3 cards at a time
    slidesToScroll: 1, // Scroll 1 card at a time
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };
  return (
    <div className="App">
      <h1>Favorite movies</h1>
     <Slider {...settings}>
     {
       data && data.length > 0 && data.map((item, index) => (
        <div key={index} className='movie-card'>
          <p className='movie-title'>Movie name: {item.title}</p>
          <p className='movie-details'>Genre: {item.genre}</p>
          <p className='movie-details'>Rating: {item.rating}</p>
          <p className='movie-details'>Release year: {item.release_year}</p>
          <p className='movie-details'>Director: {item.director}</p>
          <img src={item.image_url}/>
        </div>
      ))
     }
    </Slider>
     </div>

  );
}

export default App;
